mapper.update:
update cart
set user_id=#{userId}, product_id=#{productId}, price=#{price}, amount=#{amount}, total=#{total}
where id=#{id}

mapper.select:
select id, user_id as userId, product_id as productId, price, amount, total
from cart
where id=#{id}

select id, user_id as userId, product_id as productId, price, amount, total
from cart
where user_id=#{userId}
